private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    //Variablen 
    Double a = Double.valueOf(numInput.getText());
    int sqrt = (int)Math.sqrt(a);
    Double x = (double)sqrt;
    //Heron-Verfahren
    for(int i = 1; i <= sqrt; i++){
        //x an die reale Quadratwurzel anÃ¤hern 
        x = (x+(a/x))/2;
    }
    //Ergebnise in Feldern ausgeben
    //Reale Wurzel
    sqrtField.setText(String.valueOf(Math.sqrt(a)));
    //Gerundete Wurzel
    resultField.setText(String.valueOf(x));
}//GEN-LAST:event_jButton1ActionPerformed

